import React, { useState, useEffect } from 'react'

const sizes = [
    { id: 1, val: 'XX Small', size: 'XXS' },
    { id: 2, val: 'X Small', size: 'XS' },
    { id: 3, val: 'Small', size: 'S' },
    { id: 4, val: 'Medium', size: 'M' },
    { id: 5, val: 'Large', size: 'L' },
    { id: 6, val: 'X Large', size: 'XL' },
    { id: 7, val: 'XX Large', size: 'XXL' },
]

export default function Checkboxes({ handleChange }) {

    const [values, setValues] = useState({});

    function selectSize(e) {
        const { name, value } = e.target;
        setValues(prevValues => ({
            ...prevValues,
            [name]: value,
        }))
    }

    useEffect(() => {
        handleChange({target: {name, value: values}})
    }, values)

    return (
      <fieldset className="space-y-5">
        {sizes.map((item) => (
        <div key={item.id} className="relative flex items-start">
            <div className="flex items-center h-5">
                <input
                aria-describedby="comments-description"
                name={item.val}
                type="checkbox"
                className="focus:ring-indigo-500 h-4 w-4 text-indigo-600 border-gray-300 rounded"
                value={item.size}
                onChange={e => selectSize(e)}
                />
            </div>
            <div className="ml-3 text-sm">
                <label htmlFor="comments" className="font-medium text-gray-700">
                {item.val}
                </label>
            </div>
        </div>
        ))}
      </fieldset>
    )
  }
