let initialState = [];

export function cartReducer(state = initialState, action) {
    switch(action.type) {
        case 'ADD_TO_CART':
            console.log('Added to the cart!');
            break;
        case 'REMOVE_FROM_CART':
            console.log('Removed from the cart!');
            break;
        default:
            console.log('That is not a command!');
    }
}
