import React, { Fragment, useState } from 'react'
import { Listbox, Transition } from '@headlessui/react'
import { CheckIcon, SelectorIcon } from '@heroicons/react/solid'

const sizing = [
    { id: 1, type: 'Basic [S, M, L]', value: ['S', 'M', 'L'] },
  ]
const colours = [
    'Black',
    'Blue',
    'Green',
    'Purple',
    'Violet',
    'Indigo',
    'Pink',
    'White'
]

  function classNames(...classes) {
    return classes.filter(Boolean).join(' ')
  }

const ProductsAdmin = () => {
    const [selected, setSelected] = useState(sizing[0])
    const [colour, setColours] = useState(colours[0])

  return (
    <div className='mt-10'>
        {/* Product Name Input */}
        <lable htmlFor='product-name' className='block text-sm font-medium text-gray-500'>
            Product Name
        </lable>
        <div className='mt-1 relative rounded-md shadow-sm'>
            <input
                type='text'
                name='product-name'
                id='product-name'
                className='shadow-sm focus:ring-indigo-500 block w-full sm:text-sm border-gray-300 rounded-md'
                placeholder='Club Dress'
            />
        </div>
        {/* Sizing List */}
        <Listbox value={selected} onChange={setSelected}>
            {({ open }) => (
            <>
            <Listbox.Label className="block text-sm font-medium text-gray-700">Sizing Medium</Listbox.Label>
            <div className="mt-1 relative">
                <Listbox.Button className="bg-white relative w-full border border-gray-300 rounded-md shadow-sm pl-3 pr-10 py-2 text-left cursor-default focus:outline-none focus:ring-1 focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm">
                <span className="block truncate">{selected.type}</span>
                <span className="absolute inset-y-0 right-0 flex items-center pr-2 pointer-events-none">
                    <SelectorIcon className="h-5 w-5 text-gray-400" aria-hidden="true" />
                </span>
                </Listbox.Button>

                <Transition
                show={open}
                as={Fragment}
                leave="transition ease-in duration-100"
                leaveFrom="opacity-100"
                leaveTo="opacity-0"
                >
                <Listbox.Options className="absolute z-10 mt-1 w-full bg-white shadow-lg max-h-60 rounded-md py-1 text-base ring-1 ring-black ring-opacity-5 overflow-auto focus:outline-none sm:text-sm">
                    {sizing.map((size) => (
                    <Listbox.Option
                        key={size.id}
                        className={({ active }) =>
                        classNames(
                            active ? 'text-white bg-indigo-600' : 'text-gray-900',
                            'cursor-default select-none relative py-2 pl-3 pr-9'
                        )
                        }
                        value={size}
                    >
                        {({ selected, active }) => (
                        <>
                            <span className={classNames(selected ? 'font-semibold' : 'font-normal', 'block truncate')}>
                            {size.type}
                            </span>

                            {selected ? (
                            <span
                                className={classNames(
                                active ? 'text-white' : 'text-indigo-600',
                                'absolute inset-y-0 right-0 flex items-center pr-4'
                                )}
                            >
                                <CheckIcon className="h-5 w-5" aria-hidden="true" />
                            </span>
                            ) : null}
                        </>
                        )}
                    </Listbox.Option>
                    ))}
                </Listbox.Options>
                </Transition>
            </div>
            </>
        )}
        </Listbox>
        {/* Size Metrics Inputs */}

        {/* Colour */}
        <Listbox value={colour} onChange={setColours}>
            {({ open }) => (
            <>
            <Listbox.Label className="block text-sm font-medium text-gray-700">Product Colour/s</Listbox.Label>
            <div className="mt-1 relative">
                <Listbox.Button className="bg-white relative w-full border border-gray-300 rounded-md shadow-sm pl-3 pr-10 py-2 text-left cursor-default focus:outline-none focus:ring-1 focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm">
                <span className="block truncate">{selected.type}</span>
                <span className="absolute inset-y-0 right-0 flex items-center pr-2 pointer-events-none">
                    <SelectorIcon className="h-5 w-5 text-gray-400" aria-hidden="true" />
                </span>
                </Listbox.Button>

                <Transition
                show={open}
                as={Fragment}
                leave="transition ease-in duration-100"
                leaveFrom="opacity-100"
                leaveTo="opacity-0"
                >
                <Listbox.Options className="absolute z-10 mt-1 w-full bg-white shadow-lg max-h-60 rounded-md py-1 text-base ring-1 ring-black ring-opacity-5 overflow-auto focus:outline-none sm:text-sm">
                    {sizing.map((size) => (
                    <Listbox.Option
                        key={size.id}
                        className={({ active }) =>
                        classNames(
                            active ? 'text-white bg-indigo-600' : 'text-gray-900',
                            'cursor-default select-none relative py-2 pl-3 pr-9'
                        )
                        }
                        value={size}
                    >
                        {({ selected, active }) => (
                        <>
                            <span className={classNames(selected ? 'font-semibold' : 'font-normal', 'block truncate')}>
                            {size.type}
                            </span>

                            {selected ? (
                            <span
                                className={classNames(
                                active ? 'text-white' : 'text-indigo-600',
                                'absolute inset-y-0 right-0 flex items-center pr-4'
                                )}
                            >
                                <CheckIcon className="h-5 w-5" aria-hidden="true" />
                            </span>
                            ) : null}
                        </>
                        )}
                    </Listbox.Option>
                    ))}
                </Listbox.Options>
                </Transition>
            </div>
            </>
        )}
        </Listbox>
    </div>
  )
}

export default ProductsAdmin
