import React, { useState } from 'react'

import Tabs from '../../../components/navbar/tabs.component'
import Steps from '../../../components/navbar/steps.component'
import TextInput from '../../../components/forms/text-input.component'
import MultiPriceInput from '../../../components/forms/price-input.component'
import ImageUpload from '../../../components/forms/image-upload.component'
import Dropdown from '../../../components/forms/dropdown.component'
import SwitchToggle from '../../../components/forms/switch.component'

import { colours, categories, sizing, deliveryTime } from '../../../components/products/products.enum'

const tabs = [
    { name: 'Create', href: '#', current: true },
    { name: 'Read', href: '#', current: false },
    { name: 'Update', href: '#', current: false },
    { name: 'Delete', href: '#', current: false },
]

const steps = [
    { id: 'Step 1', name: 'Job details', href: '#', status: 'current' },
    { id: 'Step 2', name: 'Application form', href: '#', status: 'upcoming' },
    { id: 'Step 3', name: 'Preview', href: '#', status: 'upcoming' },
]

const ProductsAdmin = () => {
    const [product, setProduct] = useState({

    });

    const submitProduct = () => {
        console.log('Form submitted!');
    }

  return (
    <div className='mt-4'>
        <Tabs tabs={tabs} />
        <Steps steps={steps} />
        <main className='mt-10'>
            {/* Product Name Input */}
            <TextInput label={'Product Name'} name={'product-name'} id={'product-name'} placeholder={'Fancy Club Dress'} />
            <ImageUpload />
            {/* Sizing List */}
            <SwitchToggle />
            <Dropdown list={sizing} name={'Product Sizing'} />
            {/* Size Metrics Inputs */}
            {/* Colour */}
            <Dropdown list={colours} name={'Product Colour'} />
            {/* Pricing */}
            <MultiPriceInput label={'Pricing'} name={'product-price'} id={'product-price'} />
            {/* Delivery Time */}
            <Dropdown list={deliveryTime} name={'Delivery Time'} />
            {/* Category */}
            <Dropdown list={categories} name={'Product Category'} />
            {/* Product Features */}
            <TextInput label={'Product Features'} name={'product-feature'} id={'product-feature'} placeholder={'Anti-wrinkle'} />
            {/* Product Model Number */}
            <TextInput label={'Product Model Number'} name={'product-model-number'} id={'product-model-number'} placeholder={'MDK1927c'} />
            {/* Product Keywords */}
            <TextInput label={'Product Keywords'} name={'product-keywords'} id={'product-keywords'} placeholder={'Sexy, Club, Clean'} />
            {/* Product Sleeve Length */}
            <TextInput label={'Product Sleeve Length'} name={'product-sleeve-length'} id={'product-sleeve-length'} placeholder={'42cm'} />
            <div className='mt-4 flex justify-end'>
                <button
                    type="button"
                    className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                    onClick={submitProduct}
                >
                    Submit
                </button>
            </div>
        </main>
    </div>
  )
}

export default ProductsAdmin
